import React, { useState } from "react";

const WindowConfigurator = () => {
  const [width, setWidth] = useState("");
  const [widthTermal, setWidthTermal] = useState("");
  const [height, setHeight] = useState("");
  const [heightTermal, setHeightTermal] = useState("");
  const [front, setFront] = useState("");
  const depthFront = 20;
  const [heightFront, setHeightFront] = useState("");
  const [grid, setGrid] = useState(false);
  const [german, setGerman] = useState(false);
  const [three, setThree] = useState(false);
  const [widthThree, setWidthThree] = useState(false);
  const [anchor, setAnchor] = useState(false);


  const [dx, setDx] = useState("");
  const [sx, setSx] = useState("");  
  const [panelThickness] = useState(19); // Valore fisso
  const [frontThickness] = useState(15); // Valore fisso
  const [paintingType, setPaintingType] = useState("Aggrappante");
  const [total, setTotal] = useState(null);

  //PREZZI DEFAULT 
  var priceWood = new Map();
  priceWood.set("ABETE", 35.78 * 1.5);
  priceWood.set("LARICE", 44.17 * 1.5);
  priceWood.set("ROVERE", 40.77 * 1.5);
  priceWood.set("FRASSINO", 31.15 * 1.5);
  priceWood.set("HDF", 19.60 * 1.5);

  var fixed = 85;

  var m2 = (depth * lenght / 1000000) * 1.05;
  if(m2 < 0.25)
    m2 = 0.25;

  const calcWoodPrice = priceWood.get(essence) * m2
  var tot = 0;

  var brushPrice = 0
  if(brush)
    brushPrice = m2 * 0.20;

  tot = tot + brushPrice;

  var paintingPrice = 0;
  if(paintingType === "RAL")
    paintingPrice = ((depth * lenght + lenght * heightFront) / 1000000) * 85;
  else if(paintingType === "Mordenzato")  
    paintingPrice = ((depth * lenght + lenght * heightFront) / 1000000) * 75;

  tot+= paintingPrice;

  var m3 = ((heightFront + dx + sx) * depthFront * lenght / 1000000000) * 1.05;
  var m3Price = m3 * 1516.20;

  tot+= m3Price;
  tot+=fixed;
  tot+= calcWoodPrice;
  tot = tot * 1.02;

  const handleSubmit = (event) => {
    event.preventDefault();
    var totalPrice = tot;
    setTotal(totalPrice);

  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh", // Altezza della pagina intera
        backgroundColor: "#f5f5f5",
      }}
    >
      <div
        style={{
          display: "flex",
          gap: "20px",
          alignItems: "flex-start",
          padding: "20px",
          backgroundColor: "#ffffff",
          borderRadius: "10px",
          boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
        }}
      >
        {/* Configuratore */}
        <div
          style={{
            border: "1px solid #ccc",
            borderRadius: "8px",
            padding: "20px",
            maxWidth: "400px",
            backgroundColor: "#f9f9f9",
          }}
        >


            <form onSubmit={handleSubmit}>
              {/* Larghezza Luce architettonica */}
            <div style={{ marginBottom: "15px" }}>
              <label>
                <strong>Larghezza Luce architettonica (mm):</strong>
              </label>
              <input
                type="number"
                value={width}
                onChange={(e) => setWidth(Number(e.target.value))}
                required
              />
            </div>

            {/* Altezza Luce architettonica */}
            <div style={{ marginBottom: "15px" }}>
              <label>
                <strong>Altezza Luce architettonica (mm):</strong>
              </label>
              <input
                type="number"
                value={height}
                onChange={(e) => setHeight(Number(e.target.value))}
                required
              />
            </div>

            {/* Battuta frontale*/}
            <div style={{ marginBottom: "15px" }}>
              <label>
                <strong>Larghezza Battuta frontale:</strong>
              </label>
              <input
                type="number"
                value={front}
                onChange={(e) => setFront(Number(e.target.value))}
                required
              />
            </div>

            <div style={{ marginBottom: "15px" }}>
              <label>
                <strong>Spessore Battuta frontale in OSB (mm):</strong>
              </label>
              <input type="number" value={frontThickness} readOnly />
            </div>

            {/* Profondità spalla */}
            <div style={{ marginBottom: "15px" }}>
              <label>
                <strong>Profondità spalla:</strong>
              </label>
              <input
                type="number"
                value={depth}
                onChange={(e) => setDepth(Number(e.target.value))}
                required
              />
            </div>

            <div style={{ marginBottom: "15px" }}>
              <label>
                <strong>Spessore Spalla (mm):</strong>
              </label>
              <input type="number" value={panelThickness} readOnly />
            </div>

            {/* Dimensioni taglio termico */}
            <div style={{ marginBottom: "15px" }}>
              <label>
                <strong>Dimensioni taglio termico (larghezza):</strong>
              </label>
              <input
                type="number"
                value={widthTermal}
                onChange={(e) => setWidthTermal(Number(e.target.value))}
                required
              />
            </div>

            {/* Dimensioni taglio termico */}
            <div style={{ marginBottom: "15px" }}>
              <label>
                <strong>Dimensioni taglio termico (altezza):</strong>
              </label>
              <input
                type="number"
                value={heigthTermal}
                onChange={(e) => setHeigthTermal(Number(e.target.value))}
                required
              />
            </div>

            {/* Rete porta intonaco */}
            <div style={{ marginBottom: "15px" }}>
              <label>
                <strong>Rete porta intonaco esterna:</strong>
              </label>
              <select
                value={grid}
                onChange={(e) => setGrid(e.target.value === "true")}
              >
                <option value="false">NO</option>
                <option value="true">SI</option>
              </select>
            </div>

            {/* Falso alla tedesca */}
            <div style={{ marginBottom: "15px" }}>
              <label>
                <strong>Falso alla tedesca:</strong>
              </label>
              <select
                value={german}
                onChange={(e) => setGerman(e.target.value === "true")}
              >
                <option value="false">NO</option>
                <option value="true">SI</option>
              </select>
            </div>

            {/* Tre strati */}
            <div style={{ marginBottom: "15px" }}>
              <label>
                <strong>Tre strati da 42mm (per cardini scuri):</strong>
              </label>
              <select
                value={three}
                onChange={(e) => setThree(e.target.value === "true")}
              >
                <option value="false">NO</option>
                <option value="true">SI</option>
              </select>
            </div>

            {/*  */}
            <div style={{ marginBottom: "15px" }}>
              <label>
                <strong>Larghezza tre strati 42mm:</strong>
              </label>
              <input
                type="number"
                value={widthThree}
                onChange={(e) => setWidthThree(Number(e.target.value))}
                required
              />
            </div>

            {/* Tre strati */}
            <div style={{ marginBottom: "15px" }}>
              <label>
                <strong>Zanche:</strong>
              </label>
              <select
                value={anchor}
                onChange={(e) => setAnchor(e.target.value === "true")}
              >
                <option value="false">NO</option>
                <option value="true">SI</option>
              </select>
            </div>

            {/* Forma */}
            <div style={{ marginBottom: "15px" }}>
              <label>
                <strong>Forma falso:</strong>
              </label>
              <select value={essence} onChange={(e) => setEssence(e.target.value)}>
                <option value="RETTANGOLARE">RETTANGOLARE</option>
                <option value="TRAPEZIOIDALE">TRAPEZIOIDALE</option>
                <option value="ARCATA">ARCATA</option>
                <option value="OBLO">OBLO'</option>
              </select>
            </div>

            {/* Altri campi come da codice originale */}
            <button
              type="submit"
              style={{
                padding: "10px 20px",
                backgroundColor: "#4CAF50",
                color: "white",
                border: "none",
                borderRadius: "5px",
                cursor: "pointer",
              }}
            >
              CALCOLA PREZZO
            </button>
          </form>

          {total !== null && (
            <div
              style={{
                marginTop: "20px",
                padding: "10px",
                border: "1px solid #ccc",
                borderRadius: "8px",
                backgroundColor: "#e9f7e9",
              }}
            >
              <strong>
                TOTALE COMPLESSIVO (IVA ESCLUSA): €{total.toFixed(2)}
              </strong>
            </div>
          )}
        </div>

        {/* Immagini */}
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            gap: "20px",
            alignItems: "center",
          }}
        >
          <img
            src="img1.png"
            alt="Schema frontale"
            style={{
              maxWidth: "600px",
              border: "1px solid #ccc",
              borderRadius: "8px",
            }}
          />
          <img
            src="img2.png"
            alt="Schema laterale"
            style={{
              maxWidth: "600px",
              border: "1px solid #ccc",
              borderRadius: "8px",
            }}
          />
        </div>
      </div>
    </div>
  );
};

export default WindowConfigurator;
